    <div class="skeleton-box max-w-md w-full bg-white rounded-lg shadow-lg p-6 z-50">
      <transition name="fade">
        <div v-if="loading" class="overlay-loading flex items-center justify-center">
          <div class="sk-chase">
            <div class="sk-chase-dot" />
            <div class="sk-chase-dot" />
            <div class="sk-chase-dot" />
            <div class="sk-chase-dot" />
            <div class="sk-chase-dot" />
            <div class="sk-chase-dot" />
          </div>
        </div>
      </transition>
      <div>
        <h2 class="mt-6 text-center text-3xl leading-9 font-extrabold text-gray-900">
          Sign in to dashboard
        </h2>
      </div>
      <form class="mt-8" action="#" method="POST" @submit.prevent="submit">
        <input type="hidden" name="remember" value="true">
        <div>
          <div class="mb-3">
            <input
              v-model="form.email"
              aria-label="Email address"
              name="email"
              type="email"
              class="appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5"
              placeholder="Email address"
            >
            <div v-if="errors.email" class="text-red-400 text-xs mb-4 mt-2">
              {{ errors.email[0] }}
            </div>
          </div>
          <div>
            <input
              v-model="form.password"
              aria-label="Password"
              name="password"
              type="password"
              class="appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5"
              placeholder="Password"
            >
            <div v-if="errors.password" class="text-red-400 text-xs mb-4 mt-2">
              {{ errors.password[0] }}
            </div>
          </div>
        </div>
        <div class="mt-6 flex items-center justify-between">
          <div class="flex items-center">
            <input id="remember_me" type="checkbox" class="form-checkbox h-4 w-4 text-indigo-600 transition duration-150 ease-in-out">
            <label for="remember_me" class="ml-2 block text-sm leading-5 text-gray-900">
              Remember me
            </label>
          </div>

          <div class="text-sm leading-5">
            <NuxtLink to="/login/forgotpassword" class="font-medium text-indigo-600 hover:text-indigo-500 focus:outline-none focus:underline transition ease-in-out duration-150">
              Forgot your password?
            </NuxtLink>
          </div>
        </div>
        <div class="mt-6">
          <button type="submit" class="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm leading-5 font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-500 focus:outline-none focus:border-indigo-700 focus:shadow-outline-indigo active:bg-indigo-700 transition duration-150 ease-in-out">
            <span class="absolute left-0 inset-y-0 flex items-center pl-3">
              <svg class="h-5 w-5 text-indigo-500 group-hover:text-indigo-400 transition ease-in-out duration-150" fill="currentColor" viewBox="0 0 20 20">
                <path fill-rule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clip-rule="evenodd" />
              </svg>
            </span>
            Sign in
          </button>
        </div>
      </form>
    </div>
  </div>
</template>
<script>
export default {
  transition: 'bounce',
  layout: 'auth',
  data () {
    return {
      loading: false,
      form: {
        email: '',
        password: ''
      },
      errors: {}
    }
  },
  methods: {
    async submit () {
      this.loading = true
      await this.$auth.loginWith('laravelSanctum', { data: this.form })
        .then((response) => {
          this.$router.push('/')
        })
        .catch(({ response }) => {
          this.errors = response.data.errors
        })
        .finally(() => {
          this.loading = false
        })
    }
  }
}
</script>
